content = ""
contract_address = "0x2c4DD6281a5b0D53AE96a098b9Dd3F6B3d82a4F3"
tx = CITA::TransactionSigner.decode content
puts tx
data = tx[:unverified_transaction][:transaction][:data]

abi_hex = CitaSync::Api.get_abi(contract_address, "latest")["result"]
abi = CITA::Utils.to_bytes abi_hex
abi_hash =  Oj.load abi
tx_inputs = abi_hash.find {|item| item["signature"] == data[0...10] }
oj = Web3::Eth::Contract::ContractMethod.new(tx_inputs)
params = Web3::Eth::Abi::AbiCoder.decode_abi(oj.input_types, [data[10..data.length]].pack('H*'))
DecodeUtils.to_utf8!(params)
puts "请求ABI信息："
puts tx_inputs
puts "请求参数如下："
puts params
